mapper.select:
SELECT category , COUNT(1) AS num
FROM game_trigger


        AND TO_DAYS(create_time) = TO_DAYS(NOW()) + #{days,jdbcType=INTEGER}

    AND type = #{type,jdbcType=INTEGER}

GROUP BY category
ORDER BY category

SELECT COUNT(1) num, date_format(create_time, '%Y-%m-%d') time FROM game_trigger WHERE DATE_SUB( CURDATE() , INTERVAL 7 DAY ) <= date ( create_time )
AND game_id = #{gameId}
AND category = #{category,jdbcType=INTEGER} AND type = #{type,jdbcType=INTEGER} GROUP BY time
