mapper.select:
SELECT COUNT(1) num, date_format(create_time, '%Y-%m-%d') time FROM game_trigger WHERE DATE_SUB( CURDATE() , INTERVAL 7 DAY ) <= date ( create_time )
AND game_id = #{gameId}
AND type = #{type,jdbcType=INTEGER} GROUP BY time

SELECT COUNT(1)
FROM game_trigger
WHERE type = #{type,jdbcType=INTEGER}
and game_id =#{gameId}
